var topnav,logo,menuIcon,navOption;function topnavScroll(){80<document.body.scrollTop||80<document.documentElement.scrollTop?(topnav.style.padding="3rem 1rem",logo.style.fontSize="2.5rem"):logo.style.fontSize="3rem"}topnav=document.getElementById("top-nav"),logo=document.getElementById("logo"),menuIcon=document.getElementById("menu-icon"),navOption=document.getElementById("nav-option"),window.addEventListener("scroll",()=>{topnavScroll()}),menuIcon.addEventListener("click",()=>{navOption.style.display.match("block")?(navOption.style.display="none",document.getElementById("menu-bars").classList.remove("change")):(navOption.style.display="block",document.getElementById("menu-bars").classList.add("change"))});